###############################################################################
# BEGIN fix (221 / 362) for 'wireless_disable_interfaces'
###############################################################################
(>&2 echo "Remediating rule 221/362: 'wireless_disable_interfaces'")
# Remediation is applicable only in certain platforms
if [ ! -f /.dockerenv ] && [ ! -f /run/.containerenv ]; then

if [ -n "$(find /sys/class/net/*/ -type d -name wireless)" ]; then
    interfaces=$(ls -L -d /sys/class/net/*/wireless | xargs dirname | xargs basename)

    for i in $interfaces; do
        ifdown $i
        drivers=$(basename $(readlink -f /sys/class/net/$i/device/driver))
        echo "install $drivers /bin/true" >> /etc/modprobe.d/disable_wireless.conf
        modprobe -r $drivers
    done
    if command -v nmcli >/dev/null 2>&1 ; then
        nmcli radio all off
    fi
fi

else
    >&2 echo 'Remediation is not applicable, nothing was done'
fi
# END fix for 'wireless_disable_interfaces'

